int main(void) {
    int money;
    VI have(4);
    cin >> money;
    while (1) {
        money /= 10;
        for (int i = 0; i < 4; i++) {
            cin >> have[i];
        }
        VI cash(4, 0);
        int max10 = money;
        if (max10 > have[0]) {
            money -= have[0];
            cash[0] += have[0];
            have[0] -= have[0];
        } else {
            money -= max10;
            cash[0] += max10;
            have[0] -= max10;
        }
        if (money % 5 != 0) {
            int ooi10 = 5 - (money % 5);
            money += ooi10;
            if (cash[0] != 0) {
                cash[0] -= ooi10;
            }
            have[0] += ooi10;
        }
        int max50 = money / 5;
        if (max50 > have[1]) {
            money -= have[1] * 5;
            cash[1] += have[1];
            have[1] -= have[1];
        } else {
            money -= max50 * 5;
            cash[1] += max50;
            have[1] -= max50;
        }
        if (money % 10 == 5) {
            money += 5;
            if (cash[1] != 0) {
                cash[1] -= 1;
            }
            have[1] += 1;
        }
        int max100 = money / 10;
        if (max100 > have[2]) {
