        int uses[4] = {0,0,0,0};

        for (int i = 0; i < 4; ++i) {
            minuses[i] = 0;
        }
        int mincost = 9999;

        solve(total, n, 0, uses, &mincost);

        if (found) {
            cout << endl;
        }
        for (int i = 0; i < 4; ++i) {
            if (minuses[i]) {
                cout << vals[i] << " " << minuses[i] << endl;
            }
        }
        found = true;
    }
}

