    int p;
    int coin[4];
    int coin_value[4] = {10,50,100,500};
    cin >> p;
    if(p == 0) break;
    for(int m = 0; m < 4; m++) cin >> coin[m];

    int ans[4];
    int ans_sum = 100000;

    int i[4];
    for(i[0] = 0; i[0] <= coin[0]; i[0]++) {
      for(i[1] = 0; i[1] <= coin[1]; i[1]++) {
	for(i[2] = 0; i[2] <= coin[2]; i[2]++) {
	  for(i[3] = 0; i[3] <= coin[3]; i[3]++) {
	    int sum = 0;
	    for(int m = 0; m < 4; m++) sum += i[m] * coin_value[m];
	    if(sum < p) continue;
	    int otsuri_sum = sum - p;
	    int otsuri[4];
	    for(int m = 0; m < 4; m++) {
	      otsuri[3-m] = otsuri_sum / coin_value[3-m];
	      otsuri_sum %= coin_value[3-m];
	    }
	    bool flag = true;
	    for(int m = 0; m < 4; m++) {
	      if(i[m] > 0 && otsuri[m] > 0) {
		flag = false;
		break;
	      }
	    }
	    if(!flag) continue;
	    int newValue = 0;
	    for(int m = 0; m < 4; m++) {
	      newValue += i[m] + otsuri[m];
	    }
	    if(newValue < ans_sum) {
	      ans_sum = newValue;
	      for(int m = 0; m < 4; m++) {
		ans[m] = i[m];
	      }
	    }
	  }
	}
      }
    }

