	if(C <= num[0]*10) tmp = (C+9) / 10;
	if(tmp != 0){
	  cout << 10 << " " << tmp << endl;
	  C -= tmp * 10;
	}

	tmp = 0;
	if(C % 100 / 50 <= num[1]) tmp = C % 100 / 50;
	for(;tmp<=num[1];tmp+=2); tmp -= 2;
	if(C <= num[1]*50) tmp = (C+49) / 50;
	if(tmp != 0){
	  cout << 50 << " " << tmp << endl;
	  C -= tmp * 50;
	}

	tmp = 0;
	if(C % 500 / 100 <= num[2]) tmp = C % 500 / 100;
	for(;tmp<=num[2];tmp+=5); tmp -= 5;
	if(C <= num[2]*100) tmp = (C+99) / 100;
	if(tmp != 0){
	  cout << 100 << " " << tmp << endl;
	  C -= tmp * 100;
	}
	if(C > 0)
	  cout << 500 << " " << (C+499) / 500 << endl;
	cout << endl;
  }

  return 0;
}

