        &(d->coin500));
  return 1;
}

void solve(dataset* d) {
  int change = -(d->req);
  change += d->coin010 * 10;
  change += d->coin050 * 50;
  change += d->coin100 * 100;
  change += d->coin500 * 500;

  int r010, r050, r100, r500;

  r500 = change / 500;
  change %= 500;

  r100 = change / 100;
  change %= 100;

  r050 = change / 50;
  change %= 50;

