using namespace std;

class coin{
public:
	int n,a,b,c,d,rest;
	coin(int _n,int _a,int _b,int _c,int _d){
		n=_n;
		a=_a;
		b=_b;
		c=_c;
		d=_d;
		rest=10*a+50*b+100*c+500*d-n;
	};

	int calc(){

		int res=a+b+c+d;
		int nn=n;

		res-=nn/500;
		nn%=500;
		res-=nn/100;
		nn%=100;
		res-=nn/50;
		nn%=50;
		res-=nn/10;

		res+=rest/500;
		rest%=500;
		res+=rest/100;
		rest%=100;
		res+=rest/50;
		rest%=50;
		res+=rest/10;

		rest=10*a+50*b+100*c+500*d-n;

		return res;
	}
};


int main()
{
	int n,a,b,c,d;
	while(cin>>n){
		if(n==0) break;
		cin>>a>>b>>c>>d;
		coin ans = coin(n,a,b,c,d);

		for(int aa=0;aa<=a;aa++){
			for(int bb=0;bb<=b;bb++){
				for(int cc=0;cc<=c;cc++){
					for(int dd=0;dd<=d;dd++){
						coin tmp = coin(n,aa,bb,cc,dd);
						if(tmp.rest>=0 && ans.calc()>tmp.calc()) ans=tmp;
					}
				}
			}
		}
		if(ans.a!=0) cout<<10<<" "<<ans.a<<endl;
		if(ans.b!=0) cout<<50<<" "<<ans.b<<endl;
		if(ans.c!=0) cout<<100<<" "<<ans.c<<endl;
		if(ans.d!=0) cout<<500<<" "<<ans.d<<endl;
		cout<<endl;
	}


	return 0;
}

