int num(int c10, int c50, int c100, int c500){
  int ret=0, x=(c10*10+c50*50+c100*100+c500*500)-p;
  while(x!=0){
    if(x>=500){ret++; x-=500;}
    else if(x>=100){ret++; x-=100;}
    else if(x>=50){ret++; x-=50;}
    else if(x>=10){ret++; x-=10;}
  }
  return ret;
}

