    {
        tuple<int, int, int, int> coin; // = {0, 0, 0, 0};
        get<0>(coin) = 0;
        get<1>(coin) = 0;
        get<2>(coin) = 0;
        get<3>(coin) = 0;

        int ten, fifty, one_hundred, five_hundred, change = 0, used_coin, max = -10000;
        cin >> ten >> fifty >> one_hundred >> five_hundred;
        for (int i = 0; i < ten + 1; i++)
        {
            for (int j = 0; j < fifty + 1; j++)
            {
