// 代金と払った額からおつりを計算する関数
vector<int> calcChange(int paid){
  vector<int> cashes(4);
  if (m > paid){ cashes[0]=-1; return cashes;}
  int change = paid - m;
  cashes[3] = change / 500;
  change %= 500;
  cashes[2] = change / 100;
  change %= 100;
  cashes[1] = change / 50;
  change %= 50;
  cashes[0] = change / 10;

  return cashes;
}

int solve() {
  vector<int> answer(4);
  cin >> c10 >> c50 >> c100 >> c500;
  int min = INT_MAX;
  rep(i,c10 + 1){
    rep(j, c50 + 1){
      rep(k, c100 + 1){
        rep(l, c500 + 1){
          int paid = 10*i + 50*j + 100*k + 500*l;
          if (calcChange(paid)[0]!=-1){
            vector<int> cashes(4);
            cashes = calcChange(paid);
            int walletCounts = (c10-i+cashes[0]) + (c50-j+cashes[1]) + (c100-k+cashes[2]) + (c500-l+cashes[3]);
            // cout << "paid: " << paid << " walletCounts: " << walletCounts << " {" << cashes[0] << "," << cashes[1] << "," << cashes[2] << "," << cashes[3] << "}" << endl;
            if (min > walletCounts){
              min = walletCounts;
              answer[0]=i;
              answer[1]=j;
              answer[2]=k;
              answer[3]=l;
            }
          }
        }
      }
    }
  }

  if (answer[0] != 0){ cout << "10 " << answer[0] << endl;}
  if (answer[1] != 0){ cout << "50 " << answer[1] << endl;}
  if (answer[2] != 0){ cout << "100 " << answer[2] << endl;}
  if (answer[3] != 0){ cout << "500 " << answer[3] << endl;}


  return 0;
}

