const int MAX = 20 * 20 * 20 * 20;
int coin[4] = {10, 50, 100, 500};

int change(int a, int b, int c, int d, int p) {
  int sum = 10 * a + 50 * b + 100 * c + 500 * d - p;
  if (sum < 0) return MAX + 1;

  int res = 0;
  for (int i = 3; i >= 0; i--) {
    res += sum / coin[i];
    sum %= coin[i];
  }

  return res;
}

