	while(cin >> e, e){
		int a, b, c, d; cin >> d >> c >> b >> a;
		if(!first) cout << endl;
		first = false;
		int itr[4];
		int res[4];
		int v[4] = {500, 100, 50, 10};
		int minVal = 10000;
		for(itr[0]=0;itr[0]<=a;itr[0]++){
			for(itr[1]=0;itr[1]<=b;itr[1]++){
				for(itr[2]=0;itr[2]<=c;itr[2]++){
					for(itr[3]=0;itr[3]<=d;itr[3]++){
						int sum = 0;
						for(int i=0;i<4;i++) sum += v[i] * itr[i];
						if(sum < e) continue;
						int lest = sum - e;
						int cnt = 0;
						bool ok = true;
						for(int i=0;i<4;i++){
							int add = lest/v[i];
							if(itr[i] > 0 && add > 0) ok = false;
							cnt += add;
							lest %= v[i];
						}
						cnt += a - itr[0];
						cnt += b - itr[1];
						cnt += c - itr[2];
						cnt += d - itr[3];
						if(ok && cnt < minVal){
							minVal = cnt;
							for(int i=0;i<4;i++) res[i] = itr[i];
						}
					}
				}
			}
		}
		for(int i=3;i>=0;i--){
			if(res[i] > 0) cout << v[i] << " " << res[i] << endl;
