/* price が500円を超えていたら
 * price が500を下回るか、500円硬貨がなくなるまで使う
 * price が100を超えていたら、100を下回るか100円硬貨がなくなるまで使う。
 * 100円硬貨が使えなくなった場合
 * 価格が500円を超えていたら
 */

int main(int argc, const char * argv[]){
    while(true){
        int price;
        cin >> price;
        if(price == 0) return 0;

        int coins[4];
        cin >> coins[0] >> coins[1] >> coins[2] >> coins[3];
        int value[4] = {10, 50, 100, 500};
        int use[4] = {0, 0, 0, 0};

        int kuriage[3];

        int expected[4];

        int temp = price;
        for(int i = 3; i >= 0; i--){
            expected[i] = temp / value[i];
            temp -= value[i] * expected[i];
        }

        // 10円
        for(int i = 0; i < 4; i++){
            if(expected[i] <= coins[i]){
                use[i] = expected[i];
                coins[i] -= use[i];
            }else{
                expected[i+1]++;
            }
            switch(i){
                case 0:{
                    kuriage[0] = coins[0] / 5;
                    coins[0] %= 5;
                    coins[1] += kuriage[0];
                    break;
                }
                case 1:{
                    kuriage[1] = coins[1] / 2;
                    coins[1] %= 2;
                    coins[2] += kuriage[1];
                    break;
                }
                case 2:{
                    kuriage[2] = coins[2] / 5;
                    coins[2] %= 5;
                    coins[3] += kuriage[2];
                    break;
                }
            }
        }

        use[3] = max(0, use[3] - kuriage[2]);
        use[2] += kuriage[2] * 5;

        use[2] = max(0, use[2] - kuriage[1]);
        use[1] += kuriage[1] * 2;

        use[1] = max(0, use[1] - kuriage[0]);
        use[0] += kuriage[0] * 5;

        cout << use[0] << " "  << use[1] << " "  << use[2] << " "  << use[3] << endl;
    }
}

