	int n = n10 * 10 + n50 * 50 + n100 * 100 + n500 * 500;
	if(m > n) return -1;
	int ret = n - m;
	int res = 0;
	while(ret >= 500) {
		ret -= 500;
		++res;
	}
	while(ret >= 100) {
		ret -= 100;
		++res;
	}
	while(ret >= 50) {
		ret -= 50;
		++res;
	}
	while(ret >= 10) {
		ret -= 10;
		++res;
	}
	return res;
}


