int money_count(int m, int i, int j, int k, int l){
  tmp[0] = m/500 + in[0]-i;
  m %= 500;
  tmp[1] = m/100 + in[1]-j;
  m %= 100;
  tmp[2] = m/50 + in[2]-k;
  m %= 50;
  tmp[3] = m/10 + in[3]-l;
  return tmp[0] + tmp[1] + tmp[2] + tmp[3];
}

void solve(){
  int min_ = 100;
  for(int i=0; i<=in[0]; ++i){
    for(int j=0; j<=in[1]; ++j){
      for(int k=0; k<=in[2]; ++k){
        for(int l=0; l<=in[3]; ++l){
          int m = 500*i + 100*j + 50*k + 10*l - n;
          if(m < 0) continue;
          int sum = money_count(m, i, j, k, l);
          if(min_ > sum){
	    out[0]=i; out[1]=j; out[2]=k; out[3]=l; min_ = sum;
          }
        }
      }
    }
  }
}

