void check(int x,int y,int w,int z){
	int y10 = x10 - x;
	int y50 = x50 - y;
	int y100 = x100 - w;
	int y500 = x500 - z;
	int ans,z10,z50,z100,z500;
	z10 = z50 = z100 = z500 = 0;
	ans = (10*x + y*50 + w*100 + z*500) - n; 
	if(ans < 0)return;
	z10 = z50 = z100 = z500 = 0;
	if(ans >= 500){
		z500 = ans/500;
		ans -= 500*z500;
	}
	if(ans >= 100){
		z100 = ans/100;
		ans -= 100*z100;
	}
	if(ans >= 50){
		z50 = ans/50;
		ans -= 50*z50;
	}
	if(ans >= 10){
		z10 = ans/10;
		ans -= 10*z10;
	}
	if(res >= y10+y50+y100+y500+z10+z50+z100+z500){
		d[0] = x;
		d[1] = y;
		d[2] = w;
		d[3] = z;
		res = y10+y50+y100+y500+z10+z50+z100+z500;
	}
}

