int main(){
    while(1){
        int N;
        scanf("%d",&N);
        if(N==0)return 0;
        int n[5];// 10 円玉、50 円玉、100 円玉、500 円玉の枚数
        int n_kind[5]={10,50,100,500};
        int ans[5];
        rep(i,4){
            scanf("%d",&n[i]);
            ans[i]=0;
        }
        int nownedan=0;
        rep(i,4){
            if(nownedan>=N){
                break;
            }else{
                int usecoin=min(n[i],(  (N-nownedan)%n_kind[i])+1);
                n[i]-=usecoin;
                ans[i]+=usecoin;
                nownedan+=usecoin*n_kind[i];
            }
        }
        if(n[0]>=5){
            int usecoin=n[0]-n[0]%5;
            ans[0]+=usecoin;
        }
        if(n[1]>=2){
            int usecoin=n[1]-n[1]%2;
            ans[1]+=usecoin;
        }
        if(n[2]>=5){
            int usecoin=n[2]-n[2]%5;
            ans[1]+=usecoin;
        }
        rep(i,4){
            if(ans[i]!=0){
                printf("%d %d\n",n_kind[i],ans[i]);
            }
        }
        printf("\n");
    }
}

