VI change( int pay )
{
	VI res( 4 );
	for ( int i = 3; 0 <= i; i-- )
	{
		res[i] = pay / COINS[i];
		pay %= COINS[i];
	}
	res.PB( pay );
	return res;
}

int main()
{
