	ios::sync_with_stdio( false );

	for ( int pay; cin >> pay, pay; )
	{
		VI have( 4 );
		FOR( h, have )
		{
			cin >> h;
		}

		int resnum = INT_MAX;
		VI res( 4 );
		REP( i, 0, have[0]  + 1)
		{
			REP( j, 0, have[1] + 1 )
			{
				REP( k, 0, have[2] + 1 )
				{
					REP( l, 0, have[3] + 1 )
					{
						int total = i * COINS[0] + j * COINS[1] + k * COINS[2] + l * COINS[3];
