int money_table[N][6];

inline int minimumExchange(int m, int *nums)
{
	int t = 0;
	int ans = 0;

	t = m / 500;
	m -= 500 * t;
	nums[3] = t;
	ans += t;

	t = m / 100;
	m -= 100 * t;
	nums[2] = t;
	ans += t;

	t = m / 50;
	m -= 50 * t;
	nums[1] = t;
	ans += t;

	t = m / 10;
	m -= 10 * t;
	nums[0] = t;
	ans += t;

	return ans;
}

int main(){
	bool bFirstDataSet = true;
	int num;
	coins coin;

	num = 0;
	coin = ten;
	for(int i=0;i<N;++i){
		if(i>0&&i%9261==0){ ++num; }
		money_table[i][0] += kcoin[coin] * num;
		money_table[i][1] += num;
		money_table[i][2] = num;
	}


	num = 0;
	coin = fifty;
	for(int i=0;i<N;++i){
		if(i>0&&i%9261==0){ num = -1; }
		if(i>0&&i%441==0){ ++num; }
		money_table[i][0] += kcoin[coin] * num;
		money_table[i][1] += num;
		money_table[i][3] = num;
	}

	num = 0;
	coin = hundred;
	for(int i=0;i<N;++i){
		if(i>0&&i%441==0){ num = -1; }
		if(i>0&&i%21==0){ ++num; }
		money_table[i][0] += kcoin[coin] * num;
		money_table[i][1] += num;
		money_table[i][4] = num;
	}

	num = 0;
	coin = five_hundred;
	for(int i=0;i<N;++i){
		if(i>0&&i%21==0){ num = -1; }
		if(i>0){ ++num; }
		money_table[i][0] += kcoin[coin] * num;
		money_table[i][1] += num;
		money_table[i][5] = num;
	}

	while(true){
		int M;
		int num_total = 0;
