    for(int i = 3; i >= 0; i--){
      if(s >= b[i]){
	k[i] += s / b[i];
	s -= k[i] * b[i];
      }
    }

    for(int i = 0, f = 0; i < 4; i++, f = 0){
      if(k[i]-c[i] <= 0){
	r[i] = k[i];
	c[i] -= k[i];
      } else {
	s = b[i]*k[i];
	for(int j = i-1; j >= 0; j--){
	  if(s-c[j]*b[j] <= 0){
	    r[j] += s / b[j];
	    c[j] -= r[j];
	    f = 1;
	    break;
	  } else {
	    s -= c[j]*b[j];
	    r[j] = c[j];
	    c[j] = 0;
	  }
	}
	if(f == 0){
	  k[i+1]++;
	}
      }
    }

    for(int i = 0; i < 4; i++){
      if(r[i] > 0){
	cout << b[i] << " " << r[i] << endl;
      }
    }
  }

  return 0;
}

