int check(int ind) {
	if ((ind%2) == 0) {
		return 10 * pow(10,(int)(ind/2));
	}
	else {
		return 50 * pow(10,(int)(ind/2));
	}
}


int main(void) {
	int p;
	bool fn = false;
	cin >> p;

	while (p != 0) {
		int sum = 0;
		int change;
		std::vector<int> m(4,0);
		std::vector<int> pay(4);

		if (fn) {
			cout << endl;
		}
		else {
			fn = true;
		}

		for (int i = 0; i < 4; i++) {
			cin >> m[i];
		}

		for (int i = 0; sum < p; i++) {
			while (sum < p && m[i] > 0) {
				m[i]--;
				pay[i]++;
				sum += check(i);
			}
		}

		change = sum - p;

		for (int i = 0; i < 4; i++) {
			int temp = change/check(3-i);
			if (change/check(3-i) > 0) {
				if (pay[3-i] > 0) {
					pay[3-i] -= temp;
				}
			}
			change = change%check(3-i);
		}

		for (int i = 0; i < 4; i++) {
			if (i < 3) {
				if ((i%2) == 0) {
					if (m[i] > 4) {
						m[i+1] += m[i]/5;
						pay[i] += m[i] - m[i]%5; 
						m[i] = m[i]%5;
					}
				}
				else {
					if (m[i] > 1) {
						m[i+1] += m[i]/2;
						pay[i] += m[i] - m[i]%2; 
