using namespace std;

int n;  //支払額
int coin[4];    //各所持枚数
int mcoin[4];
int m;
int mi;
bool flag = false;

int chng(int k, int bi){
    int cnt = 0; 
    while(k / 500){
        if(bi & (1<<3)) {
            //cout << " a " << k << " " << bi << endl;
            return INF;
        }
        k -= 500;
        cnt++;
    }
    while(k / 100){
        if(bi & (1<<2)) return INF;
        k -= 100;
        cnt++;
    }
    while(k / 50){
        if(bi & (1<<1)) return INF;
        k -= 50;
        cnt++;
    }
    while(k / 10){
        if(bi & (1<<0)) return INF;
        k -= 10;
        cnt++;
    }

    return cnt;
}

int main(){

    while(cin >> n, n){
        //input
        for(int i = 0; i < 4; i++) cin >> coin[i];
