        for(int a = 0; a <= coin[0]; a++){
            if(a) bi |= 1;
            for(int b = 0; b <= coin[1]; b++){
                if(b) bi |= 1<<1;
                else bi &= ~(1<<1);
                for(int c = 0; c <= coin[2]; c++){
                    if(c) bi |= 1<<2;
                    else bi &= ~(1<<2);
                    for(int d = 0; d <= coin[3]; d++){
                        if(d) bi |= 1<<3;
                        else bi &= ~(1<<3);
                        int sum;
                        if((sum = a*10 + b*50 + c*100 + d*500) >= n){
                            m = coin[0] + coin[1] + coin[2] + coin[3] - a - b - c - d;
                            m += chng(sum - n, bi);
                            if(mi > m){
                                mi = m;
                                mcoin[0] = a;
