        if(mcoin[0])cout << 10 << ' ' << mcoin[0] << endl;
        if(mcoin[1])cout << 50 << ' ' << mcoin[1] << endl;
        if(mcoin[2])cout << 100 << ' ' << mcoin[2] << endl;
        if(mcoin[3])cout << 500 << ' ' << mcoin[3] << endl;
        flag = true;
    }

