int main(){
	int value;
	while (1){
		cin >> value;
		if (value == 0) break;
		int Coin[4] = { 10, 50, 100, 500 };
		int hasCoin[4] = {};
		int hasMoney = 0;
		for (int i = 0; i < 4; i++){
			cin >> hasCoin[i];
			hasMoney += hasCoin[i] * Coin[i];
		}
		int useCoin[4];
		int gap = hasMoney - value;
		int willHaveMoney[4] = {};
		for (int i = 3; i >= 0; i--){
			willHaveMoney[i] = gap / Coin[i];
			gap = gap % Coin[i];

			if (willHaveMoney[i] < hasCoin[i]){
				useCoin[i] = hasCoin[i] - willHaveMoney[i];
			}
			else{
				useCoin[i] = 0;
			}
		}
		for (int i = 0; i < 4; i++){
			if (useCoin[i] != 0){
				cout << Coin[i] << " " << useCoin[i] << endl;
			}
		}
		cout << endl;
	}

	return 0;
}

