void check(int x,int y,int w,int z){
	int y10 = x10 - x;
	int y50 = x50 - y;
	int y100 = x100 - w;
	int y500 = x500 - z;
	int ans,z10,z50,z100,z500;
	z10 = z50 = z100 = z500 = 0;
	ans = (10*x + y*50 + w*100 + z*500) - n; 
	if(ans < 0)return;
	z10 = z50 = z100 = z500 = 0;
