		z500 = ans/500;
		ans -= 500*z500;
	}
	if(ans >= 100){
		z100 = ans/100;
		ans -= 100*z100;
	}
	if(ans >= 50){
		z50 = ans/50;
		ans -= 50*z50;
	}
	if(ans >= 10){
		z10 = ans/10;
		ans -= 10*z10;
	}
	if(res >= y10+y50+y100+y500+z10+z50+z100+z500){
		d[0] = x;
		d[1] = y;
		d[2] = w;
		d[3] = z;
		res = y10+y50+y100+y500+z10+z50+z100+z500;
	}
