int C;
const int M[4] = {10,50,100,500};
int num[4], use[4], ans[4], res;
void dfs(int idx){
  if(idx == 4){
	int sum = 0;
	REP(i,4)
	  sum += use[i] * M[i];
	if(sum < C) return;
	sum = sum - C;
	int buf[4]; fill(buf, buf+4, 0);
	REP(i,3){
	  buf[i] = sum % M[i+1] / M[i];
	  sum -= buf[i] * M[i];
	}
	buf[3] = sum / 500;
	sum = 0;
	REP(i,4)
	  if(use[i] > 0 && buf[i] > 0) return;
	  else sum += use[i] - buf[i];
	if(res < sum){
	  res = sum;
	  REP(i,4) ans[i] = use[i];
	}
	return;
  }
  for(int i=0;i<=num[idx];++i){
	use[idx] = i;
	dfs(idx+1);
  }
}

int main(){
  cin.tie(0);
  ios_base::sync_with_stdio(false);

  bool flag = false;
  while(true){
	cin >> C; if(!C) break;
	REP(i,4) cin >> num[i];
	if(flag) cout << endl;
	res = -1;
	dfs(0);
	REP(i,4)
	  if(ans[i] > 0) cout << M[i] << " " << ans[i] << endl;
	flag = true;
  }

  return 0;
}

