using namespace std;

int otsuri(int yen)
{
    int tmp = yen, res = 0, t;
    t = tmp / 500;
    tmp -= t * 500;
    res += t;
    t = tmp / 100;
    tmp -= t * 100;
    res += t;
    t = tmp / 50;
    tmp -= t * 50;
    res += t;
    res += tmp / 10;
    return res;
}

