        int ret = 1 << 20;
        cin >> a >> b >> c >> d;


        for(int p=0;p<=a;p++){
            for(int q=0;q<=b;q++){
                for(int r=0;r<=c;r++){
                    for(int s=0;s<=d;s++){
                        v.push_back(pay(calc(p,q,r,s), p,q,r,s,p+q+r+s));
                    }
                }
            }
        }

        for(int i=0;i<v.size();i++){
            if(v[i].v >= n){
                int tmpv = val(v[i].v-n,x[0],x[1],x[2],x[3]) + v[i].sum - (v[i].p + v[i].q + v[i].r + v[i].s);
                if((x[3] && v[i].p) || (x[2] && v[i].q) || (x[1] && v[i].r) || (x[0] && v[i].s)) continue;
                if(ret >= tmpv){
                    ret = tmpv;
                    y1 = v[i].p;
                    y2 = v[i].q;
                    y3 = v[i].r;
