int w(int n){
    int m;
    if(n>0){
        return n%5;
    }else if(n == 0){
        return 0;
    }else{
        m = -n;
        m = m % 5;
        if(m==0){
            return 0;
        } else {
            return 5-m;
        }

    }
}
int w10(int n){
    if(n>=0){
        return n%10;

    }else{
        return (-n)%10;
    }
}

int y(int a, int b){
    if(a>=0){
        return a/b;
    }else {
        if((-a)%b==0){
            return -((-a)/b);
        }else {
            return -((-a)/b + 1);
        }

    }
}

