	if(cur==4){
		int sum=0;
		for(int i=0;i<4;i++)	sum+=use[i]*price[i];
		sum-=x;
		if(sum<0)	return;
		int ret=0;
		for(int i=0;i<4;i++)	ret+=have[i]-use[i];
		ret+=sum/500;	sum%=500;
		ret+=sum/100;	sum%=100;
		ret+=sum/50;	sum%=50;
		ret+=sum/10;
		if(ret<sml){
			sml=ret;
			for(int i=0;i<4;i++)	ans[i]=use[i];
		}
	}
	else{
		for(int i=0;i<=have[cur];i++){
			use[cur]=i;
			solve(cur+1);
		}
	}
}

