        rep(i,3) {
            if(c[i] * t[i] >= m) {
                if(m % t[i] == 0) {
                    k[i] = m/t[i];
                    c[i] -= k[i];
                    m = 0;
                } else {
                    k[i] = m/t[i] + 1;
                    c[i] -= k[i];
                    m = 0;
                }
            } else if(t[i]*c[i] >= m % t[i+1]) {
                k[i] += (m%t[i+1])/t[i];    
                c[i] -= k[i];
                m -= m%t[i+1];
                if(c[i] * t[i] >= t[i+1]){
                    int tmp = (c[i]/(t[i+1] / t[i]))*(t[i+1]/t[i]);
                    k[i] += tmp;
                    c[i] -= tmp;
                    m -= tmp*t[i];
                }
            } 
        }

        if(m % t[3] == 0) {
            k[3] = m / t[3];
        } else {
            k[3] = m / t[3] + 1;
        }

        rep(i,3) {
            if(c[i] * t[i] > t[i+1]) {
                k[i] += (c[i] / (t[i+1] / t[i])) * (t[i+1] / t[i]);
            }
        }

        rep(i,4) {
            if(k[i] != 0) {
                cout << t[i] << " " << k[i] << endl;
            }
        }
        cout << endl;
    }
    return 0;

}

