int cash(int a){
	int t_num[4];
	int ret = 0;
	for(int i=0;i<4;i++){
		t_num[i] = num[i];
	}
	for(int i=3;i>=0;i--){
		if(a>=item[i] && t_num[i]){
			sum[i]++;
			a-=item[i];
			t_num[i]--;
			ret++;
			i++;
		}
	}
	if(a!=0)return -1;
	return ret;
}

int plz(int a){
	int ret = 0;
	for(int i=3;i>=0;i--){
		if(a>=item[i]){
			a-=item[i];
			i++;
			ret++;
		}
	}
	return ret;
}

