int slv(int nx,int a,int b,int c,int d){

  if(nx==4){
    int temp=10*a+50*b+100*c+500*d-kin;
    if(temp<0)return(1<<21);
    int ans=0;
    while(temp/500){temp-=500;ans++;}
    while(temp/100){temp-=100;ans++;}
    while(temp/50){temp-=50;ans++;}
    while(temp/10){temp-=10;ans++;}
    return(ans+money[0]-a+money[1]-b+money[2]-c+money[3]-d);
  }

  int ans=(1<<21);
  if(nx==0)for(int i=money[nx];i>=0;i--)ans=min(ans,slv(nx+1,i,b,c,d));
  if(nx==1)for(int i=money[nx];i>=0;i--)ans=min(ans,slv(nx+1,a,i,c,d));
  if(nx==2)for(int i=money[nx];i>=0;i--)ans=min(ans,slv(nx+1,a,b,i,d));
