		int ans = 10000;
		int a_ans = 0;//10
		int b_ans = 0;//50
		int c_ans = 0;//100
		int d_ans = 0;//500

		for (int i = 0; i <= a; i++) {
			for (int j = 0; j <= b; j++) {
				for (int k = 0; k <= c; k++) {
					for (int l = 0; l <= d; l++) {
						if (i * 10 + j * 50 + k * 100 + l * 500 >= n) {
							int temp = i * 10 + j * 50 + k * 100 + l * 500 - n;
							int d_change = temp / 500;
							temp %= 500;
							int c_change = temp / 100;
							temp %= 100;
							int b_change = temp / 50;
							temp %= 50;
