                use[i] += (bill+money[i]-1)/money[i];
                bill = 0;
            }
        }

        for(int i=0; i<4; i++){
            if(use[i] > 0)
                cout << money[i] << ' ' << use[i] << endl;
        }
    }
    return 0;
}

