	while (cin >> n >> a >> b >> c >> d, n)
	{
		n /= 10;
		int cnt10 = 0, cnt50 = 0, cnt100 = 0, cnt500 = 0;
		if (a >= n % 5)
		{
			cnt10 += n % 5;
			a -= n % 5;
			n -= cnt10;
		}
		while (a > 5 && n >=5)
		{
			n -= 5;
			a -= 5;
			cnt10 += 5;
		}
		if (n%10<=b*5)
		{
			cnt50++;
			n -= 5;
			b--;
		}
		while (b >= 2 && n>=10)
		{
			cnt50 += 2;
			n -= 10;
			b -= 2;
		}

		if (n % 50 <= 10 * c)
		{
			if (n % 50 % 10 == 0) cnt100 += n % 50 / 10;
			else cnt100 += n % 50 / 10 + 1;
			n -= cnt100 * 10;
			c -= cnt100;
		}
		while (c >= 5 && n >= 50)
		{
			cnt100 += 5;
			n -= 50;
			c -= 5;
		}
		while (n > 0)
		{
			n -= 500;
			cnt500++;
			d -= 1;
		}
