		if (cnt50 > 0) cout << "50 " << cnt50 << endl;
		if (cnt100 > 0) cout << "100 " << cnt100 << endl;
		if (cnt500 > 0) cout << "500 " << cnt500 << endl;
		cout << endl;
	}
	return 0;
}

