int main() {
    int n;
    int q = 0;
    while(cin >> n, n) {
        if(q != 0) {
            cout << endl;
        }
        q++;
        int a, b, c, d;
        cin >> a >> b >> c >> d;
        int cnt = 1e9;
        int r1 = 0, r2 = 0, r3 = 0, r4 = 0;
        for(int i=0; i<=a; ++i) {
            for(int j=0; j<=b; ++j) {
                for(int k=0; k<=c; ++k) {
                    for(int l=0; l<=d; ++l) {
                        int total = 10 * i + 50 * j + 100 * k + 500 * l;
                        if(total < n) {
                            continue;
                        }
                        int m = total - n;
                        if(m / 500 > 0 && l != 0) {
                            continue;
                        }
                        int t = m / 500;
                        m %= 500;
                        if(m / 100 > 0 && k != 0) {
                            continue;
                        }
                        t += m / 100;
                        m %= 100;
                        if(m / 50 > 0 && j != 0) {
                            continue;
                        }
                        t += m / 50;
                        m %= 50;
                        if(m / 10 > 0 && i != 0) {
                            continue;
                        }
                        t += m / 10;
                        t = t + (a - i) + (b - j) + (c - k) + (d - l);
                        if(t <= cnt) {
                            r1 = i;
                            r2 = j;
                            r3 = k;
                            r4 = l;
                            cnt = t;
                        }
                    }
                }
            }
        }
        if(r1 > 0) {
            cout << 10 << ' ' << r1 << endl;
        }
        if(r2 > 0) {
            cout << 50 << ' ' << r2 << endl;
        }
        if(r3 > 0 ) {
            cout << 100 << ' ' << r3 << endl;
        }
        if(r4 > 0) {
            cout << 500 << ' ' << r4 << endl;
        }
    }
}

