#include<iostream>

using namespace std;

struct t_num_;
typedef struct t_num_ t_num;

typedef struct t_num_{
	int m10,m50,m100,m500;
	t_num operator+(const t_num&obj){
		t_num_ t;
		t.m10=this->m10+obj.m10;
		t.m50=this->m50+obj.m50;
		t.m100=this->m100+obj.m100;
		t.m500=this->m500+obj.m500;
		return t;
	}
	t_num operator-(const t_num&obj){
		t_num t;
		t.m10=this->m10-obj.m10;
		t.m50=this->m50-obj.m50;
		t.m100=this->m100-obj.m100;
		t.m500=this->m500-obj.m500;
		return t;
	}
	int sumn(){
		return m10+m50+m100+m500;
	}
	int summoney(){
		return m10*10+m50*50+m100*100+m500*500;
	}
	void print(){
		cout<<m10<<" "<<m50<<" "<<m100<<" "<<m500<<endl;
	}
}t_num;

t_num nturi(int money){
	t_num t;
	t.m500=money/500;
	money%=500;
	t.m100=money/100;
	money%=100;
	t.m50=money/50;
	money%=50;
	t.m10=money/10;
	return t;
}

int main(){
	int n,start_flag=1;

	while(cin>>n){
		if(!n)break;
		if(!start_flag)cout<<endl;
		start_flag=0;
		int e10,e50,e100,e500;
		cin>>e10>>e50>>e100>>e500;

		t_num mai={e10,e50,e100,e500};
		t_num mint={100,100,100,100};
		t_num ans;


		for(int i=0;i<=e500;i++){
			for(int j=0;j<=e100;j++){
				for(int k=0;k<=e50;k++){
					for(int l=0;l<=e10;l++){
						t_num watasu={l,k,j,i};
						if(n>watasu.summoney())continue;
						t_num ttemoto=mai-watasu;
						if(mint.sumn()>nturi(watasu.summoney()-n).sumn()+ttemoto.sumn()){
							mint=nturi(watasu.summoney()-n)+ttemoto;
							ans=watasu;
						}
					}
				}
			}
		}
		if(ans.m10)cout<<10<<' '<<ans.m10<<endl;
		if(ans.m50)cout<<50<<' '<<ans.m50<<endl;
		if(ans.m100)cout<<100<<' '<<ans.m100<<endl;
		if(ans.m500)cout<<500<<' '<<ans.m500<<endl;
	}
	return 0;
}

