tuple<int, int, int, int> f(int x){
    int n10, n50, n100, n500;
    n500 = x / 500;
    x %= 500;
    n100 = x / 100;
    x %= 100;
    n50 = x / 50;
    x %= 50;
    n10 = x / 10;
    return std::make_tuple(n10, n50, n100, n500);
}

int main(){
    std::cin.tie(nullptr);
    std::ios::sync_with_stdio(false);

    bool isFirst = true;

    while(true){
        int X, n10, n50, n100, n500;
        std::cin >> X;
        if(!X){break;}
        std::cin >> n10 >> n50 >> n100 >> n500;

        int r10 = -1, r50 = -1, r100 = -1, r500 = -1, mn = 10000;
        for(int i=0;i<=n10;++i){
            for(int j=0;j<=n50;++j){
                for(int k=0;k<=n100;++k){
                    for(int l=0;l<=n500;++l){
                        int sum = i * 10 + j * 50 + k * 100 + l * 500;
                        if(sum < X){continue;}

                        int m10, m50, m100, m500;
                        tie(m10, m50, m100, m500) = f(sum - X);

                        if(min(i, m10) >= 1 ||
                           min(j, m50) >= 1 ||
                           min(k, m100) >= 1 ||
                           min(l, m500) >= 1){
                            continue;
                        }

                        if((m10 + m50 + m100 + m500) - (i + j + k + l) < mn){
                            tie(r10, r50, r100, r500) = std::make_tuple(i, j, k, l);
                            mn = (m10 + m50 + m100 + m500) - (i + j + k + l);
                        }
                    }
                }
            }
        }

        if(!isFirst){
            std::cout << std::endl;
        }
        vector<int> u = {r10, r50, r100, r500}, v = {10, 50, 100, 500};
        for(int i=0;i<4;++i){
            if(u[i] > 0){
                std::cout << v[i] << " " << u[i] << std::endl;
            }
        }

        isFirst = false;
    }
}

