        while(coin[i]<=change){
            cnt[i]++;
            a++;
            change-=coin[i];
        }
    }
    if(cnt[0]>0&&x1>0) return 0;
    if(cnt[1]>0&&x2>0) return 0;
    if(cnt[2]>0&&x3>0) return 0;
