  int c[4],d[4],a[4],k[4],p[]={10,50,100,500};
  int n,max,m,i,j,l;
  for(i=0;scanf("%d",&n),n;i++){
    for(j=max=0;j<4;j++)scanf("%d",&c[j]);
    for(k[0]=0;k[0]<=c[0];k[0]++){
      for(k[1]=0;k[1]<=c[1];k[1]++){
	for(k[2]=0;k[2]<=c[2];k[2]++){
	  for(k[3]=0;k[3]<=c[3];k[3]++){
	    for(l=m=0;l<4;l++)m+=k[l]*p[l];//printf("%d\n",m);
	    if(m<n)continue;
	    m-=n;
	    for(l=3;l>-1;l--){
	      d[l]=m/p[l];
	      m%=p[l];
	    }
	    for(l=0;l<4;l++){
	      if(k[l]&&d[l])break;
	    }
	    if(l-4)continue;
	    for(l=m=0;l<4;l++)m+=k[l]-d[l];
	    if(max<m){
	      max=m;//printf("%d\n",max);
	      for(l=0;l<4;l++)a[l]=k[l];
	    }
	  }
	}
      }
    }
    if(i)printf("\n");//printf("%d\n",max);
    for(l=0;l<4;l++){
      if(a[l])printf("%d %d\n",p[l],a[l]);
    }
  }
  return 0;
}

