        int c[4], i, k[4], n, res[4]={0};
        if (!x) break;
        if (f) puts("");
        f=1;
        for(n=i=0;i<4;++i) scanf("%d", c+i);
        n=1<<30;
#define REP(x) for(k[x]=0;k[x]<=c[x];++k[x])
        REP(0) REP(1) REP(2) REP(3) {
            int s=0;
            for(i=0;i<4;++i) s+=y[i]*k[i];
