	}
      else
	{
	  u = s;
	  u.fifty++;
	  u.total_coins_in_purse--;
	  st[1] = dfs(u, payment - 50, cnt+1);	  
	}
    }
  if(first_state.hundred > s.hundred)
    {
      if(payment - 100 < 0)
	{
	  x = (-1) * (payment - 100);
	  //  st[2] = {s.ten, s.fifty, s.hundred++, s.five_hundreds, 0};
