      for (; amount >= 100 && L >= 2; L -= 2, Lo += 2, amount -= 100);
      for (; amount >= 500 && L >= 10; L -= 10, Lo += 10, amount -= 500);
    }
    else
      amount -= n * 50 + 100;
    if (C >= (n = amount%500/100)) {
