					}else{
						pay_50 = 0;
					}
					bill -= pay_50 * 50; 
				}
			}else{
				pay_50 = 0;
			}

			if(coin_100!=0){
				num = bill/100;
				if(bill%100!=0){
					bill = (num+1)*100;
					num++;
				}
				num = (bill%500)/100 + 5*(coin_100/5);
				if(num<=coin_100){
					pay_100 = num;
					bill -= pay_100 * 100;
				}else{
					if(num<5){
						pay_100 = 0;
					}else{
						pay_100 = num - 5;
					}
					bill -= pay_100 * 100;
				}
			}else{
				pay_100 = 0;
			}

			if(coin_500!=0 && bill>0){
				if(bill%500==0){
					pay_500 = bill/500;
				}else{
					pay_500 = bill/500 + 1;
				}
			}else{
				pay_500 = 0;
			}

			if(pay_10!=0){
				System.out.println("10 "+pay_10);
