			if(pay_50!=0){
				System.out.println("50 "+pay_50);
			}
			if(pay_100!=0){
				System.out.println("100 "+pay_100);
			}
			if(pay_500!=0){
				System.out.println("500 "+pay_500);
			}
			System.out.println();
		}
