import java.util.Scanner;

public class Main{
	static int [] k = {500,100,50,10};
	static int [] backk = new int[4];
	static int back(int b) {
		int count = 0;
		for(int i=0;i<4;i++) {
			count += b/k[i];
			backk[i] = b/k[i];
			b = b%k[i];
		}
		return count;
	}

	static boolean check(int i,int j,int k,int l) {
		if( (i!=0 && backk[0]!=0) || (j!=0 && backk[1]!=0) || (k!=0 && backk[2]!=0) || (l!=0 && backk[3]!=0) ){
			return false; 
		}else {
			return true;
		}
	}

	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);
		int p = sc.nextInt();
		while(true) {
			int [] purse = new int[4];
			int [] ans = new int[4];
			int sum = 0;
			for(int i=0;i<4;i++) {
				purse[i] = sc.nextInt();
			}
			int min = 80;
			for(int i=0;i<=purse[3];i++) {
				for(int j=0;j<=purse[2];j++) {
					for(int k=0;k<=purse[1];k++) {
						for(int l=0;l<=purse[0];l++) {
							int pay = 500*i + 100*j + 50*k + 10*l;
							if(pay>=p) {
								int b = pay - p;
								if(back(b)-(i+j+k+l)<min) {
									if(check(i,j,k,l)) {
										min = back(b)-(i+j+k+l);
										ans[0] = l;
										ans[1] = k;
										ans[2] = j;
										ans[3] = i;
									}

								}
							}
						}
					}
				}
			}
			for(int i=0;i<4;i++) {
				if(ans[i]!=0) {
					System.out.println(k[3-i]+" "+ans[i]);
				}
			}
			p = sc.nextInt();
			if(p!=0) {
				System.out.println();
			}else {
				return;
			}

		}
	}
}

// AOJ 2007 "Make Purse Light" (ICPC模擬国内予選 2005 Problem B)
import java.util.Scanner;

public class Main {
    static final int[] price = {10, 50, 100, 500};
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        while (true) {
            int pay = sc.nextInt();
            if (pay == 0) {
                break;
            }

            int[] coin = new int[4]; // coin[0-3]: 10, 50, 100, 500円硬貨の枚数
            int[] result = new int[4];
            for (int i = 0; i < coin.length; i++) {
                coin[i] = sc.nextInt();
            }

            int total = coin[0] * 10 + coin[1] * 50 + coin[2] * 100 + coin[3] * 500;
            change(total - pay, result);
            for (int i = 0; i < coin.length; i++) {
                int n = coin[i] - result[i];
                if (n > 0) {
                    System.out.println(price[i] + " " + n);
                }
            }
            System.out.println();
        }
    }

    public static void change(int total, int[] result) {
        for (int i = price.length - 1; i >= 0; i--) {
            int n = total / price[i];
            result[i] += n;
            total -= n * price[i];
        }
    }
}

import java.util.*;

public class Main{
	public static void main(String[] args){
		Scanner sc = new Scanner(System.in);
		boolean firstFlg = true;

		while(true){
			int n = sc.nextInt();
			if(n == 0) break;

			if(!firstFlg) System.out.println();
			if(firstFlg) firstFlg = false;

			int before = 0;
			int[] m = new int[4];
			for(int i=0;i<4;i++){
				m[i] = sc.nextInt();
				before += m[i];
			}

			int min = Integer.MAX_VALUE;
			int[] minPay = new int[4];

			for(int i=0;i<=m[0];i++){
				for(int j=0;j<=m[1];j++){
					for(int k=0;k<=m[2];k++){
						for(int l=0;l<=m[3];l++){
							int ex = exchange(n,i,j,k,l);
							if(ex == Integer.MAX_VALUE) continue;

							int after = before - (i+j+k+l) + ex;
							if(after < min){
								minPay[0] = i;
								minPay[1] = j;
								minPay[2] = k;
								minPay[3] = l;
							}
						}
					}
				}
			}

			if(minPay[0]!=0) System.out.println("10 " + minPay[0]);
			if(minPay[1]!=0) System.out.println("50 " + minPay[1]);
			if(minPay[2]!=0) System.out.println("100 " + minPay[2]);
			if(minPay[3]!=0) System.out.println("500 " + minPay[3]);
		}
	}

	private static int exchange(int n,int i,int j,int k,int l){
		int ex = n - i*10 - j*50 - k*100 - l*500;
		if(ex > 0) return Integer.MAX_VALUE;

		ex = -ex;

		int res = 0;
		int tmp;

		if((tmp=ex/500) > 0 && l > 0) return Integer.MAX_VALUE;
		res += tmp;
		if((tmp=ex%500/100) > 0 && k > 0) return Integer.MAX_VALUE;
		res += tmp;
		if((tmp=ex%100/50) > 0 && j > 0) return Integer.MAX_VALUE;
		res += tmp;
		if((tmp=ex%50/10) > 0 && i > 0) return Integer.MAX_VALUE;
		res += tmp;

		return res;
	}
}

